import { useState } from "react";

const Form = () => {
    const [show,setShow] = useState(false);
    const [fullName, setFullName] = useState({
        fname: '',
        lname: '',
    })
    const onSubmit = (event) => {
        event.preventDefault();
    }
    const inputEvent = (event) => {
        const {name,value} = event.target;
        name === 'fname' ? 
        setFullName((preval) => ({fname:value, lname:preval.lname})) :
        setFullName((preval) => ({fname:preval.fname, lname:value})) ;

    }
    return (
        <>
            {
                show && 
                <div>
                    <div>{`first name : ${fullName.fname}`}</div>
                    <div>{`last name : ${fullName.lname}`}</div>
                </div>
            }
            <form onSubmit={onSubmit}>
                <input type='text' name='fname' placeholder='Enter your first name' value={fullName.fname} onChange={inputEvent}/>
                <input type='text' name='lname' placeholder='Enter your last name' value={fullName.lname} onChange={inputEvent}/>
                <button type='submit'>Submit</button>
            </form>
        </>
    )
}
export default Form;
